// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract UniqueArt {
    address public ownerAddress;
    mapping(uint256 => address) public approvedAddress;

    event NFTMinted(address indexed to, uint256 indexed tokenId);
    event OwnershipTransferred(address indexed from, address indexed to, uint256 indexed tokenId);
    event ApprovalGranted(address indexed approved, uint256 indexed tokenId);

    constructor() {}

    function mintNFT(address to, uint256 tokenId) public {
        ownerAddress = to;
        emit NFTMinted(to, tokenId);
    }

    function transferNFT(address from, address to, uint256 tokenId) public {
        ownerAddress = to;
        emit OwnershipTransferred(from, to, tokenId);
    }

    function approve(address approved, uint256 tokenId) public {
        approvedAddress[tokenId] = approved;
        emit ApprovalGranted(approved, tokenId);
    }
}

